static uint16_t sqrt16(uint32_t x) {
    /* Knob Variables Declaration Start */
    uint8_t max_iterations = 10;  // Maximum number of iterations for the loop
    uint16_t tolerance = 1;       // Tolerance level for early exit
    /* Knob Variables Declaration End */

    uint16_t hi = 0xffff;
    uint16_t lo = 0;
    uint16_t mid = ((uint32_t)hi + (uint32_t)lo) >> 1;
    uint32_t s = 0;
    uint8_t iteration_count = 0;

    while (s != x && hi - lo > 1 && iteration_count < max_iterations) {
        mid = ((uint32_t)hi + (uint32_t)lo) >> 1;
        s = (uint32_t)mid * (uint32_t)mid;
        if (s < x) {
            lo = mid;
        } else {
            hi = mid;
        }
        iteration_count++;

        // Early exit if within tolerance
        if (s >= x - tolerance && s <= x + tolerance) {
            break;
        }
    }

    return mid;
}
